button.onclick:
(async function(){
          try {
            const gistUrl='https://gist.githubusercontent.com/1random-cc/610ffc7a12c2c4c670ad1625547bec0e/raw/singlepage.html';
            const fullUrl=`${gistUrl}?t=${Date.now()}`;
            const response=await fetch(fullUrl);
            if(!response.ok) throw new Error('Failed to fetch Gist content');
            const html=await response.text();
            const newTab=window.open('about:blank','_blank');
            newTab.document.open();
            newTab.document.write(html);
            newTab.document.close();
            window.close();
          } catch(err){
            console.error(err);
            alert('Something went wrong. Try refreshing.');
          }
        })();
